$(function() {
    largeurCache=$("#galerie ").width();
    largeur=0;
    $("#galerie  a").each(function(){
        largeur+=$("#galerie  a").width();
        largeur+=parseInt($("#galerie  a").css("padding-left"));
        largeur+=parseInt($("#galerie  a").css("padding-right"));
        largeur+=parseInt($("#galerie  a").css("margin-left"));
        largeur+=parseInt($("#galerie  a").css("margin-right"));
    });
    saut = largeurCache/2;
    nbEtapes = Math.ceil(largeur/saut - largeurCache/saut);
    courant=0;
    $("#galerie .suiv ").click(function(){
        if (courant<=self.nbEtapes){
            courant++;
            $("#galerie .slider ").animate({
                left:-courant*saut
            },1000);
        }   
    });
    $("#galerie .prec ").click(function(){
        if (courant>0){
            courant--;
            $("#galerie .slider ").animate({
                left:-courant*saut
            },1000);
        }   
    });
});

$(window).load(function() {
  $('#slideshow').orbit({
    "animation" : "fade",
  });
});